mapper.select:
select * from shopping_cart


        and id = #{id}


        and name = #{name}


        and image = #{image}


        and user_id = #{userId}


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}


        and number = #{number}


        and amount = #{amount}


        and create_time = #{createTime}


order by create_time desc
